import './App.css';
import React, { useState } from 'react';

const PatternSVG = ({ selectedRegion, onSelect, regionColors }) => {
  return (
    <svg
      viewBox="0 0 200 200"
      width="300"
      height="300"
      style={{ border: '1px solid #ccc', cursor: 'pointer' }}
    >
      <rect x="0" y="0" width="200" height="200" fill="#f0f0f0" />

      <path
        id="region-1"
        d="M10,10 h80 v80 h-80 z"
        fill={regionColors['region-1']}
        stroke={selectedRegion === 'region-1' ? 'black' : 'none'}
        strokeWidth="2"
        onClick={() => onSelect('region-1')}
      />
      <path
        id="region-2"
        d="M110,10 h80 v80 h-80 z"
        fill={regionColors['region-2']}
        stroke={selectedRegion === 'region-2' ? 'black' : 'none'}
        strokeWidth="2"
        onClick={() => onSelect('region-2')}
      />
      <path
        id="region-3"
        d="M60,110 h80 v80 h-80 z"
        fill={regionColors['region-3']}
        stroke={selectedRegion === 'region-3' ? 'black' : 'none'}
        strokeWidth="2"
        onClick={() => onSelect('region-3')}
      />
    </svg>
  );
};

function App() {
  const [selectedRegion, setSelectedRegion] = useState('');
  const [regionColors, setRegionColors] = useState({
    'region-1': '#ff9999',
    'region-2': '#99ccff',
    'region-3': '#99ff99',
  });

  const handleColorChange = (color) => {
    if (!selectedRegion) return;
    setRegionColors((prev) => ({
      ...prev,
      [selectedRegion]: color,
    }));
  };

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '1rem' }}>
      <PatternSVG
        selectedRegion={selectedRegion}
        onSelect={(id) => setSelectedRegion(id)}
        regionColors={regionColors}
      />

      <div style={{ display: 'flex', gap: '0.5rem' }}>
        {['#f87171', '#60a5fa', '#34d399', '#fbbf24', '#a78bfa'].map((color) => (
          <button
            key={color}
            onClick={() => handleColorChange(color)}
            style={{
              width: 40,
              height: 40,
              backgroundColor: color,
              border: selectedRegion ? '2px solid black' : '1px solid #ccc',
              cursor: selectedRegion ? 'pointer' : 'not-allowed',
            }}
            disabled={!selectedRegion}
          />
        ))}
      </div>

      <p>
        {selectedRegion
          ? `Selected region: ${selectedRegion}`
          : 'Click a region to select it, then pick a color.'}
      </p>
    </div>
  );
}

export default App;
