import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [svgContent, setSvgContent] = useState('');
  const [selectedElement, setSelectedElement] = useState(null);
  const svgContainerRef = useRef(null);

  // Handle SVG File Upload
  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file && file.type.includes('svg')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setSvgContent(e.target.result); // raw SVG string
      };
      reader.readAsText(file);
    } else {
      alert('Please upload a valid SVG file');
    }
  };

  // Attach click handlers to SVG paths after loading
  useEffect(() => {
  if (!svgContent || !svgContainerRef.current) return;

  const svgEl = svgContainerRef.current.querySelector('svg');
  if (!svgEl) return;

  if (!selectedElement) return;
  selectedElement.setAttribute('stroke', 'black');
  selectedElement.setAttribute('stroke-width', '2');

  return () => {
    selectedElement.removeAttribute('stroke');
    selectedElement.removeAttribute('stroke-width');
  };

  const clickableElements = svgEl.querySelectorAll('path, rect, circle, polygon');

  clickableElements.forEach((el) => {
    el.style.cursor = 'pointer';

    const handleClick = () => {
      setSelectedElement(el);
    };

    el.addEventListener('click', handleClick);

    // Cleanup to prevent duplicate listeners when SVG changes
    return () => {
      el.removeEventListener('click', handleClick);
    };
  });
}, [svgContent]);

  // Change color of selected path
  const changeColor = (color) => {
    if (selectedElement) {
      selectedElement.setAttribute('fill', color);
    }
  };

  return (
    <div className="App" style={{ textAlign: 'center', padding: '1rem' }}>
      <h2>SVG Recoloring Demo</h2>
      <input type="file" accept=".svg" onChange={handleFileUpload} />

      <div
        ref={svgContainerRef}
        dangerouslySetInnerHTML={{ __html: svgContent }}
        style={{ marginTop: '1rem', border: '1px solid #ccc', display: 'inline-block' }}
      />

      {selectedElement && (
        <div style={{ marginTop: '1rem' }}>
          <p>Selected: {selectedElement.tagName}</p>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '0.5rem' }}>
            {['#f87171', '#60a5fa', '#34d399', '#fbbf24', '#a78bfa'].map((color) => (
              <button
                key={color}
                onClick={() => changeColor(color)}
                style={{
                  width: 40,
                  height: 40,
                  backgroundColor: color,
                  border: '1px solid #333',
                  cursor: 'pointer',
                }}
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
